-- Blog Artikel mit Bildern laden
SELECT *
FROM blog_articles AS b
JOIN images AS i 
ON b.thumbnail_img = i.img_id


SELECT * FROM shop_product AS p 
JOIN images AS img ON p.product_img = img.img_id 
JOIN shop_product_attributevalue AS a0 ON p.product_id = a0.product_id 
JOIN shop_product_attributevalue AS a1 ON p.product_id = a1.product_id 
JOIN shop_product_attributevalue AS a2 ON p.product_id = a2.product_id 
JOIN shop_product_attributevalue AS a3 ON p.product_id = a3.product_id 
WHERE a0.attributevalue_id = '1' AND a1.attributevalue_id = '3' AND a2.attributevalue_id = '6' AND a3.attributevalue_id = '10'ORDER BY p.product_id ASC